import { Link } from 'react-router-dom';
import heroImage from '../assets/image1.png'; // Make sure image exists

import './Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <div className="home-text">
        <h1>Welcome to Truth Engine</h1>
        <p>Your AI-powered assistant for uncovering the truth behind trending topics.</p>
        <Link to="/analysis" className="cta-button">Start Analyzing</Link>
      </div>
      <div className="home-image">
        <img src={heroImage} alt="AI Analysis Visualization" />
      </div>
    </div>
  );
};

export default Home;
