import './Dashboard.css';

const Dashboard = () => {
  const stats = {
    topicsAnalyzed: 128,
    fakeNewsCount: 42,
    mostCommonEmotion: 'Angry'
  };

  const recentTopics = [
    'Farm Bill Protest',
    'Election Exit Polls',
    'Budget 2025 Announcement',
    'Celebrity Death Hoax',
    'Viral School Incident'
  ];

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-heading">üìä Dashboard Overview</h2>

      <div className="stat-cards">
        <div className="stat-card">
          <h3>{stats.topicsAnalyzed}</h3>
          <p>Topics Analyzed</p>
        </div>
        <div className="stat-card">
          <h3>{stats.fakeNewsCount}</h3>
          <p>Fake News Flags</p>
        </div>
        <div className="stat-card">
          <h3>{stats.mostCommonEmotion}</h3>
          <p>Trending Emotion</p>
        </div>
      </div>

      <div className="recent-topics">
        <h3 className="recent-heading">üïµÔ∏è‚Äç‚ôÇÔ∏è Recent Topics</h3>
        <ul className="recent-list">
          {recentTopics.map((topic, idx) => (
            <li key={idx} className="recent-item">{topic}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
