import React from 'react';
import './Dashboard.css';
import image1 from '../assets/image1.png'; // Make sure this path is correct

const Dashboard = () => {
  const stats = {
    topicsAnalyzed: 128,
    fakeNewsCount: 42,
    mostCommonEmotion: 'Angry',
  };

  const recentTopics = [
    'Farm Bill Protest',
    'Election Exit Polls',
    'Budget 2025 Announcement',
    'Celebrity Death Hoax',
    'Viral School Incident',
  ];

  return (
    <div className="dashboard-container">
      <h2>Dashboard Overview</h2>

      {/* Image Section */}
      <div className="dashboard-image-container" aria-label="Dashboard visual representation">
        <img
          src={image1}
          alt="Illustration representing dashboard data"
          className="dashboard-image"
        />
      </div>

      {/* Statistics Cards */}
      <div className="stat-cards">
        <div className="card" tabIndex={0} aria-label={`${stats.topicsAnalyzed} topics analyzed`}>
          <h3>{stats.topicsAnalyzed}</h3>
          <p>Topics Analyzed</p>
        </div>
        <div className="card" tabIndex={0} aria-label={`${stats.fakeNewsCount} fake news flags`}>
          <h3>{stats.fakeNewsCount}</h3>
          <p>Fake News Flags</p>
        </div>
        <div className="card" tabIndex={0} aria-label={`Trending emotion is ${stats.mostCommonEmotion}`}>
          <h3>{stats.mostCommonEmotion}</h3>
          <p>Trending Emotion</p>
        </div>
      </div>

      {/* Recent Topics List */}
      <div className="recent-topics">
        <h3>Recent Topics</h3>
        <ul>
          {recentTopics.map((topic, idx) => (
            <li key={idx}>{topic}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
